    def fix_sting_format(self,str):
        import re
        return "".join(re.split('\xa0|\n|\.|\t|', str))

    def scrape_entire_front_page(self,page):
        from bs4 import BeautifulSoup
        import requests
        proxy_urls = {
            "http": "http://proxy:8118"
        }
        response = requests.get(url=f"http://strongerw2ise74v3duebgsvug4mehyhlpa7f6kfwnas7zofs3kov7yd.onion/all?page={page}",
                                proxies=proxy_urls)
        web_text = response.text
        website = BeautifulSoup(web_text, "lxml")
        return website

    def scrape_new_pastes_data(self, website):
        ner_analyzer = NamedEntityRecognition()
        data = []
        for paste in website.find_all(name="div", class_="col-sm-12"):
            try:
                title = self.fix_sting_format(paste.findNext('h4').text.strip().split('\n')[0])
                content = self.fix_sting_format(paste.findNext(name="div", class_="well well-sm well-white pre").text.strip())
                footer = paste.findNext(name="div", class_="col-sm-6").text.split('at')
                author = self.fix_sting_format("".join(footer[0].split(' ')[2:]))
                date = "".join(self.fix_sting_format(footer[1]).split(',')).strip()
                ner_object = ner_analyzer.return_ner_analysis(content)
                data.append({"title":title,"content":content,"author":author, "date":date, "sentiment":self.calculateSentiment(content.lower()+title.lower()), "paste_entire_content":f"{title.lower()} {content.lower()} {author.lower()}", "ner":ner_object})
            except:
                pass
        return data

